import {StyleSheet, View, Text, TouchableOpacity} from 'react-native'
import * as React from 'react';
import { render } from 'react-dom';
import {Comment} from '../components/Comment.js'


export function Thread({route,navigation}) {
    const {id} = route.params

    class CommentThread extends React.Component {
        constructor(props) {
            super(props)
        }
        state = {
            comments: []
        }

        componentDidMount() {
            axios.get(SETTINGS.url+'/comment/')
              .then((response)=>{
                const comments = response.data
                this.setState({comments})
              })
        }
        render() {
            const { navigate } = this.props.navigation;  
            const { comments } = this.state
            return (
                <View>
                    <Text>test {id}</Text>
                    {
                        comments.map((comment)=><Comment id={comment.id} post_id={comment.post_id} parent_id={comment.parent_id} text={comment.text} user_id={comment.user_id} navigation={{navigate}}/>)
                    }
                </View>
            )
        }
    } 
    return (<CommentThread/>)
}
